    //Variable declaration
    //Number to decide player's turn
    public int turnNum = 0;
    //Boolean to decide what to set text on buttons to
    public boolean turn;
    //Winner variable
    public boolean xWinner;
    public boolean oWinner;
    //Winner counting variable
    public int xWinCount;
    public int oWinCount;
    //Method creation
    public void play(){
        //Checks if the turnNum is equal or odd
        if(turnNum % 2 == 0){
            turn = true;
        } else {
            turn = false;
        }
        
        //Increase turn int for next player's turn
        turnNum++;
    }
    
    //Updates text based on turn boolean
    public void updateText(JButton btn){
        if(turn == true){
            btn.setText("X");   
        } else {
            btn.setText("O"); 
        }
        
    }
    
    public int[][] board = {
        {0, 0, 0},
        {0, 0, 0},
        {0, 0, 0}
    };
    
    public void checkWin(){
        int turnCheck;
        if(turn == true){
            turnCheck = 1;
        } else {
            turnCheck = 2;
        }
        
        //Runs checkboard function based on turn variable
        checkBoard(turnCheck);
    }
    
    public void checkBoard(int check){
        //Checks every possible win combo
        //Checks horizontal win combos
        checkTop(check);
        checkMid(check);
        checkBot(check);
        //Left vertical
        if(board[0][0] == check && board[1][0] == check && board[2][0] == check) {
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
            
        //Middle vertical
        } else if(board[0][1] == check && board[1][1] == check && board[2][1] == check){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        //Right vertical
        } else if(board[0][2] == check && board[1][2] == check && board[2][2] == check){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        //Top right to bottom left diagonal
        } else if(board[0][0] == check && board[1][1] == check && board[2][2] == check){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        //Top left to bottom right diagonal
        } else if(board[0][2] == check && board[1][1] == check && board[2][0] == check){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        }
        
    }
    
    public void checkTop (int check){
        //Local int for win check
        int trueCount = 0;
        //Checks top row
        for(int i = 0; i<3; i++){
            if(board[0][i] == check){
                trueCount++;
            }
        }     
        
        //Give win code
        if(trueCount == 3){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        }
    }
    
    public void checkMid(int check){
        //Local int for win check
        int trueCount = 0;
        
         //Checks middle row
        for(int x = 0; x<3; x++){
            if(board[1][x] == check){
                trueCount++;
            }
        }
        
        //Give win code
        if(trueCount == 3){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        }
    }
    
    public void checkBot(int check){
        //Local int for win check
        int trueCount = 0;
        
         //Checks bottom row
        for(int y = 0; y<3; y++){
            if(board[2][y] == check){
                trueCount++;
            }
        } 
        
        //Give win code
        if(trueCount == 3){
            if(check == 1){
                xWinner = true;
            } else {
                oWinner = true;
            }
        }
    }
    
    public void updateBoard(int slotY, int slotX){
        int playerNum = 0;
        if(turn == true){
            playerNum = 1;
        } else {
            playerNum = 2;
        }
        //Update board array to match inputs
        board[slotY][slotX] = playerNum;
    }
    
    public void winnerDecision(){
        if(xWinner == true){
            //Increase win counter
            xWinCount++;
            //Updates win counter text
            lblXWinCount.setText("X Wins: " + xWinCount);
            //Resets the board
            boardReset(); 
        } else if(oWinner == true){
            //Increases win counter
            oWinCount++;
            //Updates win counter text
            lblOWinCount.setText("O Wins: " + oWinCount);
            //Resets the board
            boardReset();
        }
    }
    
    public void boardReset(){
        //Resets top row
        for(int i = 0; i<3; i++){
            board[0][i] = 0;
        }
        //Resets middle row
        for(int x = 0; x<3; x++){
            board[1][x] = 0;
        }
        //Resets bottom row
        for(int y = 0; y<3; y++){
            board[2][y] = 0;
        }
        
        //Resets text displayed on buttons
        btnSlot1.setText("");
        btnSlot2.setText("");
        btnSlot3.setText("");
        btnSlot4.setText("");
        btnSlot5.setText("");
        btnSlot6.setText("");
        btnSlot7.setText("");
        btnSlot8.setText("");
        btnSlot9.setText("");
        
        //Resets win variables
        xWinner = false;
        oWinner = false;
        
        //Reset turn variable
        turnNum = 0;
    }
   
